#  One-cell solution: filter LabelMe JSON annotations in Google Drive

from google.colab import drive
drive.mount('/content/drive')

import json, sys, shutil
from pathlib import Path

# ---------------------------
#  CONFIG (edit these paths)
# ---------------------------
INPUT_DIR  = "/content/drive/MyDrive/datasets/labelme_raw"   # where your JSONs are
OUTPUT_DIR = "/content/drive/MyDrive/datasets/labelme_filtered"  # where results go
CLASSES    = ["crack", "bughole"]   #  change to ["crack"], ["bughole"], or both
RECURSIVE  = True
COPY_IMAGES = True
SKIP_EMPTY = True
IGNORE_CASE = True
# ---------------------------

def list_json_files(root: Path, recursive=True):
    return [p for p in (root.rglob("*.json") if recursive else root.glob("*.json"))]

def ensure_dir(path: Path): path.mkdir(parents=True, exist_ok=True)

def filter_shapes(data, include_labels, ignore_case):
    if include_labels is None: return data.get("shapes", [])
    if ignore_case:
        inc = {lbl.lower() for lbl in include_labels}
        return [s for s in data.get("shapes", []) if isinstance(s.get("label"), str) and s["label"].lower() in inc]
    return [s for s in data.get("shapes", []) if s.get("label") in include_labels]

def copy_image_if_needed(data, in_json, out_json):
    if not COPY_IMAGES: return
    if data.get("imageData"): return
    img_field = data.get("imagePath")
    if not img_field: return
    src, dst = (in_json.parent / img_field).resolve(), (out_json.parent / Path(img_field).name).resolve()
    if src.exists():
        ensure_dir(dst.parent); shutil.copy2(src, dst)

def save_filtered_json(data, shapes, out_path):
    new_data = dict(data); new_data["shapes"] = shapes
    ensure_dir(out_path.parent)
    with out_path.open("w", encoding="utf-8") as f: json.dump(new_data, f, ensure_ascii=False, indent=2)

# ---------------------------
#  PROCESS ALL FILES
# ---------------------------
in_root, out_root = Path(INPUT_DIR).resolve(), Path(OUTPUT_DIR).resolve()
json_files = list_json_files(in_root, recursive=RECURSIVE)
print(f"Found {len(json_files)} JSONs under {in_root}")

kept_counts, total, written, skipped = {}, 0, 0, 0
for in_json in json_files:
    total += 1
    rel, out_json = in_json.relative_to(in_root), out_root / rel
    try:
        data = json.loads(in_json.read_text(encoding="utf-8"))
    except Exception as e:
        print(f"[warn] Cannot read {in_json}: {e}"); continue

    shapes = filter_shapes(data, CLASSES if CLASSES else None, IGNORE_CASE)
    if SKIP_EMPTY and not shapes:
        skipped += 1; continue

    for s in shapes:
        lbl = s.get("label", "__unknown__")
        kept_counts[lbl] = kept_counts.get(lbl, 0) + 1

    save_filtered_json(data, shapes, out_json)
    copy_image_if_needed(data, in_json, out_json)
    written += 1

print("\n===== SUMMARY =====")
print(f"Input folder : {in_root}")
print(f"Output folder: {out_root}")
print(f"JSON scanned : {total}")
print(f"JSON written : {written}")
if SKIP_EMPTY: print(f"Skipped empty: {skipped}")
print("Kept shapes  :", kept_counts if kept_counts else "None")



