"""
Batch overlay of LabelMe-style JSON annotations on images (with translucent fills).

- JSONs in one folder (e.g., /data/json/)
- Images in another (e.g., /data/jpg/)
- Matches by filename stem: 7001-68.json  <->  7001-68.jpg/.jpeg/.png
"""

from pathlib import Path
import argparse
import json
from typing import List, Tuple, Dict, Optional

from PIL import Image
import matplotlib.pyplot as plt
from matplotlib.patches import Polygon
from matplotlib.lines import Line2D

# -------------------- CONFIG: per-class style --------------------
# Edit/extend with your class names and colors.
CLASS_STYLE: Dict[str, dict] = {
    "bughole": dict(edgecolor="#e53935", linewidth=2.5, linestyle="-"),   # red
    "crack":   dict(edgecolor="#1e88e5", linewidth=2.5, linestyle="--"),  # blue dashed
    # "spall": dict(edgecolor="#43a047", linewidth=2.5, linestyle="-"),   # green
}
DEFAULT_STYLE = dict(edgecolor="#000000", linewidth=2.0, linestyle="-")   # fallback

# ---- Translucent fills enabled ----
FILL_ALPHA = 0.25             # 0.25 transparency
FILL_COLOR_PER_CLASS = True   # facecolor = edgecolor
# -----------------------------------

def rect_to_polygon_pts(p1, p2):
    (x1, y1), (x2, y2) = p1, p2
    x_min, x_max = min(x1, x2), max(x1, x2)
    y_min, y_max = min(y1, y2), max(y1, y2)
    return [(x_min, y_min), (x_max, y_min), (x_max, y_max), (x_min, y_max)]

def find_image_for_stem(images_dir: Path, stem: str) -> Optional[Path]:
    for ext in (".jpg", ".jpeg", ".png", ".JPG", ".JPEG", ".PNG"):
        p = images_dir / f"{stem}{ext}"
        if p.exists():
            return p
    return None

def draw_one(json_file: Path, images_dir: Path, out_dir: Path) -> None:
    with open(json_file, "r") as f:
        data = json.load(f)

    stem = json_file.stem
    img_path = None

    # Prefer imagePath from JSON (basename only), else match by stem
    img_rel = data.get("imagePath")
    if img_rel:
        img_path = find_image_for_stem(images_dir, Path(img_rel).stem)
    if img_path is None:
        img_path = find_image_for_stem(images_dir, stem)
    if img_path is None:
        print(f"[WARN] No matching image found for {json_file.name}")
        return

    img = Image.open(img_path).convert("RGB")

    fig, ax = plt.subplots(figsize=(8, 8), dpi=200)
    ax.imshow(img)
    ax.set_axis_off()

    legend_handles: Dict[str, Line2D] = {}

    for shape in data.get("shapes", []):
        label = shape.get("label", "unknown")
        pts = shape.get("points", [])
        if not pts or len(pts) < 2:
            continue

        shape_type = (shape.get("shape_type") or "").lower()

        # Build vertex list
        if shape_type == "rectangle" and len(pts) >= 2:
            verts = rect_to_polygon_pts(pts[0], pts[1])
            closed = True
        else:
            verts = [(float(x), float(y)) for x, y in pts]
            closed = shape_type in ("polygon", "rectangle") or verts[0] == verts[-1]

        style = CLASS_STYLE.get(label, DEFAULT_STYLE).copy()

        # Fill uses class color (if enabled) and only applies to closed shapes
        face = style["edgecolor"] if FILL_COLOR_PER_CLASS else "none"
        do_fill = (FILL_ALPHA > 0.0 and face != "none" and closed)

        poly = Polygon(
            verts,
            closed=closed,
            fill=do_fill,
            facecolor=face if do_fill else "none",
            alpha=FILL_ALPHA if do_fill else 1.0,
            **style
        )
        ax.add_patch(poly)

        if label not in legend_handles:
            legend_handles[label] = Line2D(
                [0], [0],
                color=style["edgecolor"],
                linewidth=style.get("linewidth", 2.0),
                linestyle=style.get("linestyle", "-")
            )

    if legend_handles:
        ax.legend(legend_handles.values(), legend_handles.keys(),
                  loc="upper right", frameon=True)

    out_dir.mkdir(parents=True, exist_ok=True)
    out_path = out_dir / f"{stem}_annotated.jpg"
    fig.tight_layout()
    fig.savefig(out_path, bbox_inches="tight")
    plt.close(fig)
    print(f"[OK] Saved: {out_path}")

def main():
    parser = argparse.ArgumentParser(description="Batch annotate images from separate JSON and image folders.")
    parser.add_argument("--json_dir", required=True, type=Path, help="Folder containing .json files")
    parser.add_argument("--images_dir", required=True, type=Path, help="Folder containing .jpg/.jpeg/.png files")
    parser.add_argument("--out_dir", required=True, type=Path, help="Output folder for annotated images")
    args = parser.parse_args()

    json_files = sorted(args.json_dir.glob("*.json"))
    if not json_files:
        print(f"[WARN] No .json files found in {args.json_dir}")
        return

    for jf in json_files:
        try:
            draw_one(jf, args.images_dir, args.out_dir)
        except Exception as e:
            print(f"[ERROR] {jf.name}: {e}")

if __name__ == "__main__":
    main()